import React, { useEffect, useState } from "react";  // Importe o useState e useEffect
import { useNavigate } from "react-router-dom";
import Logo from "../assets/logos/logo_preta.png"; // Importe a logo

const Header = () => {
  const [username, setUsername] = useState("");  // Defina o estado para o nome do usuário
  const navigate = useNavigate(); // Caso precise de navegação em algum momento

  useEffect(() => {
    // Recupera o nome do usuário armazenado no localStorage
    const storedUsername = localStorage.getItem("username");
    setUsername(storedUsername && storedUsername !== "undefined" ? storedUsername : "Usuário"); // Atualiza o estado com o nome
  }, []);  // Apenas executa na primeira renderização

  return (
    <header className="fixed inset-x-0 top-0 z-40 bg-[#FAF9F7] shadow-sm">
      <div className="mx-auto flex max-w-screen-xl items-center justify-between px-8 py-4">
        {/* Logo */}
        <div className="flex items-center">
          <img src={Logo} alt="NutriHouse" className="h-20 w-auto" />
        </div>

        {/* Navbar */}
        <nav className="flex space-x-2 items-center">
          <div className="relative">
            <a href="#" className="text-black font-medium hover:text-yellow-600">
              CURSOS
            </a>
            <div className="absolute left-0 w-full h-1 bg-yellow-400 rounded mt-1" />
          </div>
          <a href="#" className="text-black font-medium hover:text-yellow-600">
            SOBRE
          </a>
        </nav>

        {/* Saudação ao Usuário */}
        <div className="flex items-center space-x-2">
          <span className="text-black">
            Olá, <strong>{username}</strong> {/* Exibe o nome do usuário */}
          </span>
          <div className="w-6 h-6 bg-black rounded-full"></div> {/* Ícone do usuário */}
        </div>
      </div>
    </header>
  );
};

export default Header;
