import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import CarouselCard from "../components/CarouselCard";
import carouselData from "../data/carouselData";
import Header from "../components/Header";
import styles from "../style/Home.module.css";

const PAGE_SIZE = 6; // 3x2

// Função para dividir o array de dados em páginas
const chunk = (arr, size) =>
  Array.from({ length: Math.ceil(arr.length / size) }, (_, i) =>
    arr.slice(i * size, i * size + size)
  );

const Home = () => {
  const [username, setUsername] = useState("Usuário");
  const [page, setPage] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const stored = localStorage.getItem("username");
    if (stored && stored !== "undefined") setUsername(stored);
  }, []);

  const pages = chunk(carouselData, PAGE_SIZE);
  const total = pages.length;
  const current = pages[page] ?? [];

  // Função de clique no card
  const handleCardClick = (id) => {
    console.log(`Card ${id} clicado`);
    // Exemplo de navegação futura:
    // navigate(`/curso/${id}`);
  };

  return (
    <main className="min-h-screen bg-[#FAF9F7] flex flex-col">
      <Header username={username} />

      <section className={`${styles.carouselArea}`}>
        <div className={`${styles.container} ${styles.carouselWrapper} px-6`}>
          {/* Grade 3x2 */}
          <div className="grid grid-cols-3 gap-6">
            {current.map((item, i) => (
              <div key={i} className="flex justify-center">
                <CarouselCard
                  title={item.title}
                  icon={item.image}
                  description={item.description}
                  onClick={() => handleCardClick(item.id)}
                />
              </div>
            ))}
          </div>

          {/* Setas para navegação */}
          <div className="flex justify-between mt-6">
            {/* Botão de seta para voltar */}
            <button
              onClick={() => setPage(page > 0 ? page - 1 : total - 1)}
              className="p-2 bg-gray-300 rounded-full hover:bg-gray-400 focus:outline-none"
              aria-label="Voltar para a página anterior"
            >
              <span className="text-2xl font-bold">&lt;</span>
            </button>

            {/* Dots de navegação */}
            <div className={styles.dotsRow}>
              {Array.from({ length: total }).map((_, i) => (
                <button
                  key={i}
                  onClick={() => setPage(i)}
                  aria-label={`Ir para página ${i + 1}`}
                  className={`h-2 w-2 rounded-full transition-all ${
                    page === i
                      ? "w-4 bg-gray-800"
                      : "bg-gray-300 hover:bg-gray-400"
                  }`}
                />
              ))}
            </div>

            {/* Botão de seta para avançar */}
            <button
              onClick={() => setPage(page < total - 1 ? page + 1 : 0)}
              className="p-2 bg-gray-300 rounded-full hover:bg-gray-400 focus:outline-none"
              aria-label="Avançar para a próxima página"
            >
              <span className="text-2xl font-bold">&gt;</span>
            </button>
          </div>
        </div>
      </section>
    </main>
  );
};

export default Home;
