import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Slider from "react-slick";
import CarouselCard from "../components/CarouselCard";
import carouselData from "../data/carouselData";
import Header from "../components/Header";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Home = () => {
  const [username, setUsername] = useState("Usuário");
  const navigate = useNavigate();

  useEffect(() => {
    const stored = localStorage.getItem("username");
    if (stored && stored !== "undefined") setUsername(stored);
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    centerMode: true,
    centerPadding: "0px",
    slidesToShow: 6,
    slidesToScroll: 6,
    arrows: true,
    responsive: [
      { breakpoint: 1024, settings: { slidesToShow: 6, slidesToScroll: 6 } },
      { breakpoint: 768,  settings: { slidesToShow: 2, slidesToScroll: 2 } },
      { breakpoint: 480,  settings: { slidesToShow: 1, slidesToScroll: 1 } },
    ],
  };

  return (
    <main className="min-h-screen bg-neutral-50 flex flex-col">
      {/* Header só aqui no Home */}
      <Header username={username} />

      {/* Faixa central: ocupa o restante da tela e centraliza o carrossel */}
      <section className="flex-1 w-full flex items-center justify-center">
        <div className="w-full max-w-screen-xl mx-auto px-4">
          <Slider {...settings}>
            {carouselData.map((item, i) => (
              <div key={i} className="px-4 flex justify-center">
                <CarouselCard title={item.title} icon={item.image} />
              </div>
            ))}
          </Slider>

          <div className="flex justify-center mt-8">
            <button
              onClick={() => navigate("/cards")}
              className="px-6 py-3 rounded-xl bg-rose-500 text-white font-medium shadow-md hover:shadow-lg transition"
            >
              Ver todos
            </button>
          </div>
        </div>
      </section>
    </main>
  );
};

export default Home;
