import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Slider from "react-slick";
import CarouselCard from "../components/CarouselCard";
import carouselData from "../data/carouselData";
import styles from "../style/Home.module.css";
import Header from "../components/Header";  // Importando Header

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Home = () => {
  const [username, setUsername] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    setUsername(
      storedUsername && storedUsername !== "undefined"
        ? storedUsername
        : "Usuário"
    );
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 6,  // Exibe 6 cards por vez
    slidesToScroll: 6,  // Rola 6 cards por vez
    arrows: true,
    centerMode: false,  // Não precisa de centralização
    responsive: [
      {
        breakpoint: 1024, // Em telas maiores que 1024px, mantém 6 slides
        settings: {
          slidesToShow: 6,
          slidesToScroll: 6,
        },
      },
      {
        breakpoint: 768, // Em telas menores que 768px, ajusta para 2 slides
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
        },
      },
      {
        breakpoint: 480, // Em telas menores que 480px, ajusta para 1 slide
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  return (
    <main className={`${styles.container} pt-16 px-24`}>
      {/* Exibindo o Header apenas na página Home */}
      <Header username={username} />  {/* Passando o nome do usuário para o Header */}

      {/* Carrossel */}
      <div className="w-full flex justify-center items-center">
        <Slider {...settings}>
          {carouselData.map((item, index) => (
            <div key={index} className="p-4">
              <CarouselCard title={item.title} icon={item.image} />
            </div>
          ))}
        </Slider>
        <div className={styles.buttonWrapper}>
          <button
            onClick={() => navigate("/cards")}
            className={styles.seeAllButton}
          >
            Ver todos
          </button>
        </div>
      </div>

      {/* Cards organizados em 2 linhas com 3 cards cada */}
      <div className="grid grid-cols-3 gap-1 mt-10">
        {carouselData.slice(0, 6).map((item, index) => (
          <CarouselCard key={index} title={item.title} icon={item.image} />
        ))}
      </div>
    </main>
  );
};

export default Home;
