import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Slider from "react-slick";
import CarouselCard from "../components/CarouselCard";
import carouselData from "../data/carouselData";
import Header from "../components/Header";

import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Home = () => {
  const [username, setUsername] = useState("Usu치rio");
  const navigate = useNavigate();

  useEffect(() => {
    const stored = localStorage.getItem("username");
    if (stored && stored !== "undefined") setUsername(stored);
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    centerMode: false,
    centerPadding: "0px",
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: true,
    responsive: [
      { breakpoint: 1024, settings: { slidesToShow: 1, slidesToScroll: 1 } },
      { breakpoint: 768,  settings: { slidesToShow: 1, slidesToScroll: 1 } },
      { breakpoint: 480,  settings: { slidesToShow: 1, slidesToScroll: 1 } },
    ],
  };

// helper para paginar em grupos de N (6)
const chunk = (arr, size) =>
  Array.from({ length: Math.ceil(arr.length / size) }, (_, i) =>
    arr.slice(i * size, i * size + size)
  );

const pages = chunk(carouselData, 6);

  return (
    <main className="min-h-screen bg-neutral-50 flex flex-col">
      {/* Header s칩 aqui no Home */}
        <Header username={username} />
          {/* Faixa central: ocupa o restante da tela e centraliza o carrossel */}
        <section className="flex-1 w-full flex items-center justify-center">
          <div className="w-full max-w-screen-xl mx-auto px-4">
            
          <Slider {...settings}>
            {pages.map((page, idx) => (
              <div key={idx}>
                {/* 3 colunas fixas, 2 linhas autom치ticas */}
              <div className="grid grid-cols-3 gap-6">
                {page.map((item, i) => (
                    <CarouselCard key={i} title={item.title} icon={item.image} />
                  ))}
                </div>
              </div>
            ))}
          </Slider>

          <div className="flex justify-center mt-8">
            <button
              onClick={() => navigate("/cards")}
              className="px-6 py-3 rounded-xl bg-rose-500 text-white font-medium shadow-md hover:shadow-lg transition"
            >
              Ver todos
            </button>
          </div>
        </div>
      </section>
    </main>
  );
};

export default Home;
